import './App.css';
import React from 'react'

export class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      currentItem: {
        text: '',
        key: ''
      }
    }
  }

  render() {



    return (
      <div className='App'>
        <header>
          <form id="to-do-list">
            <input type='text' placeholder='Enter Your Text'  value={this.state.currentItem} onchange={this.handleInput}/>
            <button type="submit">ADD</button>
          </form>
        </header>

      </div>
    )
  }
}



export default App;
